catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') {
		return;
	}

	//Type appropriate comment here, and begin script below   //Type appropriate comment here, and begin script below

	var itemList = g_form.getValue('hidden_field');
	var holder ="";
	var splitCategory = [];
	var splitName = [];
	var splitID = [];
	var index=0;
	if(itemList != null || itemList !=""){
		g_form.clearOptions('item');
		splitCategory = itemList.split(',');

		for(var i =0; i < splitCategory.length; ++i){
			index = splitCategory[i].indexOf('-');
			holder += splitCategory[i].substring((index+1)) +",";
			splitCategory[i] = splitCategory[i].substring(0, index);
		}
		splitID = holder.split(',');
		holder = "";

		for(i=0;i<splitID.length; ++i){
			index = splitID[i].indexOf('+');
			holder += splitID[i].substring((index+1)) +",";
			splitID[i] = splitID[i].substring(0, index);

		}

		splitName = holder.split(',');

		for(i=0; i < splitCategory.length; ++i){
			if(newValue == splitCategory[i]){
				g_form.addOption("item", splitID[i], splitName[i]);
			}
		}


		g_form.setVisible('item', true);
		g_form.setMandatory('item', true);
	}
	else{
		g_form.clearOptions('item');
		g_form.addOption("item", "none", "No items available");
		g_form.setVisible('item', true);
	}


	/*var ga = new GlideAjax('HardwareLoanerScriptDriver');  //ga for GlideAjax : Just in case ;)
	ga.addParam('sysparm_name', "getItems");
	ga.addParam("sysparm_category_choice", newValue);
	ga.addParam("sysparm_date_choice", g_form.getValue('check_out'));
	ga.getXMLAnswer(ajaxCallback);*/



}

function ajaxCallback(answer){
	if(answer != null){
		var splitAnswer = answer.split(',');
		g_form.clearOptions('item');
		//splitAnswer[0] = splitAnswer[0].slice(9);
		splitAnswer.sort();
		for(var i=0; i < splitAnswer.length; ++i){
			var x = splitAnswer[i].substring(0,33);
			splitAnswer[i] = splitAnswer[i].slice(35);
			g_form.addOption("item", x, splitAnswer[i]);
		}
		g_form.setVisible('item', true);
		g_form.setMandatory('item', true);
	}
	else{
		g_form.clearOptions('item');
		g_form.addOption("item", "none", "No items available");
		g_form.setVisible('item', true);
	}
}
