catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') {
		return;
	}

	//Type appropriate comment here, and begin script below
	var department = newValue;
	var ga = new GlideAjax('HardwareLoanerScriptDriver');  //ga for GlideAjax : Just in case ;)
	ga.addParam('sysparm_name', "noRequestItems");
	ga.addParam('sysparm_department', newValue);

	ga.getXMLAnswer(ajaxCallback);



	function ajaxCallback(answer){
		//console.log("First Call: " + answer);
		//console.log(answer);

		var splitAnswer;
		var i=0;
		//console.log("This is the answer: " + answer);
		var ga = new GlideAjax('HardwareLoanerScriptDriver');  //ga for GlideAjax : Just in case ;)
		var firstAnswer = "";
		firstAnswer = answer;
		ga.addParam('sysparm_item_lists', firstAnswer);
		ga.addParam("sysparm_department_choice", newValue);

		//console.log("Check Out Date: "+ g_form.getValue('check_out'));
		ga.addParam("sysparm_date_return", g_form.getValue('due_date'));
		ga.addParam("sysparm_date_choice", g_form.getValue('check_out'));
		ga.addParam('sysparm_name', "getCategory");
		ga.getXMLAnswer(secondCallAjax);

	}


	function secondCallAjax(answer){
		console.log("Answer Second Call: "+answer);
		if(answer != null){
			if(answer!="weekend_checkout"){
				if(answer!="weekend_return"){
					var ga = new GlideAjax('HardwareLoanerScriptDriver');  //ga for GlideAjax : Just in case ;)
					ga.addParam('sysparm_name', "shortQuery");
					ga.addParam('sysparm_item', answer);
					ga.getXMLAnswer(thirdCallAjax);

				}
				else{
					g_form.clearValue('due_date');					
					g_form.showFieldMsg("due_date", "Items must be returned on a workday, please select a non-weekend date.", 'error');
					g_form.addOption("Category", "none", "No items available");
					g_form.setVisible('Category', true);
					g_form.setVisible('item', false);
				}

			}
			else{
				g_form.clearValue('check_out');
				g_form.showFieldMsg("check_out", "No items available for this checkout date, please select a non-weekend date.", 'error');
				g_form.addOption("Category", "none", "No items available");
				g_form.setVisible('Category', true);
				g_form.setVisible('item', false);

			}

		}
		else{
			g_form.clearValue('check_out');
			g_form.showFieldMsg("check_out", "No items available for this date, please select a new date.", 'error');
			g_form.addOption("Category", "none", "No items available");
			g_form.setVisible('Category', true);
			g_form.setVisible('item', false);
		}
	}


	function thirdCallAjax(answer){
		var splitAnswer = [];
		//console.log("Answer Third Call: " + answer);

		splitAnswer = answer.split(',');
		for(var i =0; i < splitAnswer.length; ++i){
			var index = splitAnswer[i].indexOf('-');
			splitAnswer[i] = splitAnswer[i].substring(0,index);
		}
		g_form.clearOptions("Category");
		g_form.setValue('hidden_field', answer, ' ');
		splitAnswer.sort();
		for(var x=0; x < splitAnswer.length; ++x){
			g_form.addOption("Category", splitAnswer[x], splitAnswer[x]);
			//console.log("split answer: "+splitAnswer[x]);
		}
		g_form.setVisible('Category', true);
		g_form.setMandatory('Category', true);
	}
}
