catalog_script_client.script:
function onLoad() {
	//This is for preparing the form for use.  It will load in the Loaner Department dropdown.
	var groups;
	var ga = new GlideAjax('HardwareLoanerScriptDriver');  //ga for GlideAjax : Just in case ;)
	//Hiding Fields that do not have items populated yet.
	g_form.setVisible('Category', false);
	g_form.setVisible('item',false);
	g_form.setVisible('hidden_field',false);
	ga.addParam('sysparm_name', 'getDepartment');
	ga.getXMLAnswer(ajaxCallback);

}

function ajaxCallback(answer){
	//var jsonArray = new JSON.parse(answer);
	var splitAnswer = answer.split(',');
	splitAnswer[0] = splitAnswer[0].slice(9);
	splitAnswer.sort();
	for(var i=0; i < splitAnswer.length; ++i){
		g_form.addOption("Loaner_Department", splitAnswer[i], splitAnswer[i]);
	}
}
