catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
	if (isLoading || newValue == '') {
		return;
	}

	//Type appropriate comment here, and begin script below
	if(g_form.getValue('due_date') !=""){
		var ga = new GlideAjax('HardwareLoanerScriptDriver');
		ga.addParam('sysparm_name', "dateValidation");
		ga.addParam('sysparm_in', g_form.getValue('due_date'));
		ga.addParam('sysparm_out', g_form.getValue('check_out'));
		console.log('Due Date: '+ g_form.getValue('due_date'));
		ga.getXMLAnswer(CallAjax);
	}
}

function CallAjax(answer){
	console.log("Date Validation: " + answer);
	if(answer == 'false'){
		g_form.showFieldMsg("due_date", "Due date must be after pick up date.", 'error');
	}
}
