sys_script_client.script:
function onLoad() {
	//This is setting role specific fields.
	if(!g_user.hasRole("itil")){
		//g_form.setReadOnly("closed_by", true);
	}
	else{
		//g_form.setReadOnly("priority", false);
	}

	//This is to set approval high lights.	
	if(g_form.getValue('approval') == "not requested"){
		g_form.getControl('approval').style["background-color"]= '#ff9999';
	}
	else if(g_form.getValue('approval') == "requested"){
		g_form.getControl('approval').style["background-color"]= '#ffff99';
	}
	else if(g_form.getValue('approval') == "approved"){
		g_form.getControl('approval').style["background-color"]= '#66ff8c';
	}
}

