sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	//Type appropriate comment here, and begin script below
	if(newValue == 3 || newValue == 4 || newValue == 5){
		var ga = new GlideAjax('HardwareLoanerScriptDriver');
		ga.addParam('sysparm_name', 'checkTask');
		ga.addParam('sysparm_elr_number', g_form.getValue('number'));
		ga.getXMLAnswer(ajaxCallback);

	}


	function ajaxCallback(answer){
		//console.log("First Call: " + answer);

		var ga = new GlideAjax('HardwareLoanerScriptDriver');
		ga.addParam('sysparm_name', 'checkTaskTwo');
		ga.addParam('sysparm_elr_id', answer);
		ga.getXMLAnswer(ajaxCallbackTwo);
	}

	function ajaxCallbackTwo(answer){
		//console.log("Second Call: " + answer);
		if(answer == 'true'){
			console.log("oldVal: " + oldValue);
			g_form.setValue('state', oldValue);
			g_form.showFieldMsg('state', "There are still tasks active please close them first.", 'error');
		}
	}
}
