sys_script_client.script:
function onLoad() {
   //Type appropriate comment here, and begin script below
	var ga = new GlideAjax('HardwareLoanerScriptDriver');  //ga for GlideAjax : Just in case ;)
		ga.addParam('sysparm_name', "getTaskCount");
		ga.addParam("sysparm_record", g_form.getValue("number"));
		ga.getXMLAnswer(ajaxCallback); 
}

function ajaxCallback(answer){
	//var jsonArray = new JSON.parse(answer);
	console.log("Task Answer: " + answer);
	var splitAnswer = answer.split(',');
	if((splitAnswer.length -1) > 2){
		g_form.setReadOnly("cancelled",true);
		g_form.setReadOnly("cancelled_by", true);
	}
	else{
		g_form.setReadOnly("cancelled",false); 
		g_form.setReadOnly("cancelled_by", false);
	}
}
