// This script needs to set answer to 'yes' or 'no' to indicate the state of the activity.
answer = ifScript();

function ifScript() {
	var memberGroup = new GlideRecord('sys_user_grmember');
	if(current.requested_for !=""){
		memberGroup.addActiveQuery();
		memberGroup.addQuery('user', current.requested_for);
		memberGroup.query();
		
		while(memberGroup.next()){
			if (memberGroup.group.name == 'Approval Exemption Group' || memberGroup.group.name == 'SN_Managers') {
				return 'yes';
			}
		}
		return 'no';
	}
	else{	
		memberGroup.addActiveQuery();
		memberGroup.addQuery('user', current.opened_by);
		memberGroup.query();
		
		while(memberGroup.next()){
			if (memberGroup.group.name == 'Approval Exemption Group' || memberGroup.group.name == 'SN_Managers') {
				return 'yes';
			}
		}
		return 'no';
	}
	
}

